(ns day1.day1part1
  (:require [ysera.test :refer [is is=]]))


;
;
;    (()) and ()() both result in floor 0.
;    ((( and (()(()( both result in floor 3.
;    ))((((( also results in floor 3.
;    ()) and ))( both result in floor -1 (the first basement level).
;    ))) and )())()) both result in floor -3.
(defn day1part1
  {:test (fn []
           (is= (day1part1 "(())") 0)
           (is= (day1part1 "()()") 0)
           (is= (day1part1 "(((") 3)
           (is= (day1part1 "())") -1)
           (is= (day1part1 "))(") -1)
           (is= (day1part1 ")))") -3)
           (is= (day1part1 ")())())") -3))}
  [string]
  (->> string
       (reduce
         (fn [acc paranthesis]
           (condp = paranthesis
             \) (dec acc)
             \( (inc acc)

             acc
             ))
         0)))

(defn day1part2
  {:test (fn []
           (is= (day1part2 ")") 1)
           (is= (day1part2 "()())") 5))}
  [string]
  (as-> string $
        (map-indexed (fn [idx char] [idx char]) $)
        (loop [acc 0
               [item & items] $]
          (let [acc (condp = (second item)
                      \) (dec acc)
                      \( (inc acc)

                      acc)]
            (if (= -1 acc)
              (inc (first item))
              (recur acc items))))))
